import axios from "axios";

const API_KEY = "YOUR_OPENWEATHERMAP_API_KEY"; // Replace this with your API key
const BASE_URL = "https://api.openweathermap.org/data/2.5";

export const getCurrentWeather = async (city) => {
  const response = await axios.get(`${BASE_URL}/weather`, {
    params: {
      q: city,
      appid: API_KEY,
      units: "metric",
    },
  });
  return response.data;
};

export const getHistoricalWeather = async (lat, lon) => {
  const timestamps = Array.from({ length: 5 }).map((_, i) =>
    Math.floor(Date.now() / 1000 - i * 86400)
  );

  const requests = timestamps.map((ts) =>
    axios.get(`${BASE_URL}/onecall/timemachine`, {
      params: {
        lat,
        lon,
        dt: ts,
        appid: API_KEY,
        units: "metric",
      },
    })
  );

  const responses = await Promise.all(requests);
  return responses.map((res) => res.data);
};
